package AssignmentProblem;

public class DefaultValusofPrimitiveDataTypes {
	static byte val1;
	static short val2;
	static int val3;
	static long val4;
	static float val5;
	static double val6;
	static boolean val7;
	static String val8;

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Default Values ");
		System.out.println("Byte : "+ val1);
		System.out.println("Short : "+ val2);
		System.out.println("Integer : "+ val3);
		System.out.println("Long : "+ val4);
		System.out.println("Float : "+ val5);
		System.out.println("Double : "+ val6);
		System.out.println("Boolean : "+ val7);
		System.out.println("Character : "+ val8);

	}

}
